import React from "react";

function ProgressBar({ session }) {

    return (
        <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={ (session.currentTime - session.timer) / session.currentTime * 100} 
                style={{ width: `${(session.currenTime - session.timer) / session.currentTime * 100}%` }} // Increase width % as elapsed time increases
              />
        </div>
    )
}

export default ProgressBar;
