import React from "react";
import { secondsToDuration } from "../utils/duration";
// import ProgressBar from "./ProgressBar";

function Countdown ({ session, isTimerRunning }) {
    if (!session.active) return null;

    return (
        <div>
            <div className="row mb-2">
                <div className="col">
                    <h2 data-testid="session-title">{session.sessionTypeTitle} for {secondsToDuration(session.currentTime)} minutes</h2>
                    <p className="lead" data-testid="session-sub-title">
                        {secondsToDuration(session.timer)} remaining
                    </p>
                    <h3>{!isTimerRunning && `PAUSED`}</h3>
                </div>
            </div>
            <div className="row mb-2">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={(session.currentTime - session.timer) / session.currentTime * 100} 
                style={{ width: `${(session.currentTime - session.timer) / session.currentTime * 100}%` }} // Increase width % as elapsed time increases
              />
        </div>
            </div>
        </div>
    )
}

export default Countdown;
