import React from "react";
import { secondsToDuration } from "../utils/duration";
import { classNames } from "../utils/class-names";

function BreakDuration({min, max, label, session, setSession}) {
    const decreaseButtonHandler = () => {
        if (session.breakTime > min) (setSession({...session, breakTime: session.breakTime - 60}));
    };

    const increaseButtonHandler = () => {
        if (session.breakTime < max) (setSession({...session, breakTime: session.breakTime + 60}));
    };

    return (
        <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-break">
                {/* display the current break session time */}
                {`${label} ${secondsToDuration}`}
            </span>
        </div>
    )
}

export default BreakDuration;
